// htmlImageBrowserCreator#include "htmlImageBrowserCreator.h"map<string, GOption> Page::defineOptions() {	map<string, GOption> optionsMap;	optionsMap["addTableVariable"]     = GOption("Table Variable Description",   "run", "general", true);	optionsMap["addQuantityVariable"]  = GOption("QuantityVariable Description", "run", "general", true);	optionsMap["f"]                    = GOption("Filename", "imageslist.txt", "general", true);	return optionsMap;}Page::Page(int argc, char *argv[]){	// last argument: quit if a command line option is not found in the map	gopts = new GOptions(argc, argv, defineOptions(), 0);		vector<string> tableVariables = gopts->getValues("addTableVariable");		for(auto &tv: tableVariables) {		cout <<  " tableVariables " << tv << endl;	}																	 			}